import React from 'react'
import useFetch from '../hooks/useFetch';

type DataFromFetch = {
    albumId: number;
    id: number;
    thumbnailUrl:string;
    title: string;
    url: string;
    };

type DataArray = {data:Array<DataFromFetch>};    

const Albums = ():JSX.Element => {

    const {responseData, error, isLoading} = useFetch<DataArray>('http://localhost:3002/api/albums');
    const {data} = responseData as DataArray;

    if (isLoading) {
      return <div>Loading...</div>;
    }
    if (error) {
      return <div>Error: {error as string}</div>;
    }

    const albumList = data.map((album) => {
        return (
          <div key={album.id}>
            <p>{album.title}</p>
            <p>{album.url}</p>
            <p>{album.thumbnailUrl}</p>
          </div>
        );
    })

  return(<>
    {albumList}
  </>);
};

export default Albums;
