import React, {useEffect, useState} from 'react'
import useFetch from '../hooks/useFetch';

type ItemProps = {
    item:string;
    price: number;
    };

// type MerchArray = {data:Array<DataFromStore>};    
const FormItem = new FormEvent<ItemProps>({
    item: '',
    price: 0
});

const AddItem = ():JSX.Element => {
    const [item, setItem] = useState<ItemProps | null>(null);
    const handleItem = (e:FormData) => {
        e.preventDefault(); 
        // console.log(e.currentTarget.name, e.currentTarget.value)
        // setItem({item: e.currentTarget.na, price: 100});
    };

    useEffect(() => {
        if(item === null) return;
        fetch('http://localhost:3002/addItem', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(item)
        })
    }, [item]);



  return(<div style={{ display: "flex"}}>
    <form>
        <label>Item
            <input type="text" name="item" placeholder='item name'/>
        </label>
        <label>Price
            <input type="number" name="price" placeholder='price'/>
        </label>
        <input type="submit" onSubmit={(e) => handleItem} value="Submit"/>
    </form>
  </div>);
};

export default AddItem;
