import React, {useState} from 'react'
import useFetch from '../hooks/useFetch';

type DataFromStore = {
    id: string;
    item:string;
    price: number;
    };

type MerchArray = {data:Array<DataFromStore>};    

const Store = ():JSX.Element => {

    const {responseData, error, isLoading} = useFetch<MerchArray>('http://localhost:3002/store');

    if (isLoading) {
      return <div>Loading...</div>;
    }
    if (error) {
      return <div>Error: {error as string}</div>;
    }

    const storeList = responseData?.data.map((items, index) => {
        const {id, item, price} = items;
        return (
            <div key={id} style={{ flex: "0 0 33.33%", padding: "10px" }}>
            <p key={item+index}>{item}</p>
            <p key={price+index}>{price}</p>
          </div>
        );
    })
    // create a flex grid of 3x3 with the storeList

  return(<div style={{ display: "flex", flexWrap: "wrap" }}>
    {storeList}
  </div>);
};

export default Store;
