import React from 'react'
import useFetch from '../hooks/useFetch';

type Animals = {
    name:string;
    src:string;
    location:string
}

type AnimalsArray = {data:Array<Animals>};    

const Animals = ():JSX.Element => {

    const {responseData, error, isLoading} = useFetch<AnimalsArray>('http://localhost:3002/api/data');
    const {data} = responseData as AnimalsArray;

    if (isLoading) {
      return <div>Loading...</div>;
    }
    if (error) {
      return <div>Error: {error as string}</div>;
    }

    const animalList = data.map((animal) => {
        return (
            <div key={animal.name} style={{ flex: "0 0 33.33%", padding: "10px" }}>
            {/* Replace the content below with your desired album rendering */}
            <img src={animal.src} alt={animal.name} style={{ width: "100%", height: "auto" }} />
            <p>{animal.location}</p>
          </div>
        );
    })
    // create a flex grid of 3x3 with the albumList

  return(<div style={{ display: "flex", flexWrap: "wrap" }}>
    {animalList}
  </div>);
};

export default Animals;
