import useFetch from '../hooks/useFetch';

type ItemProps = {
    item:string;
    price: number;
    };

// type MerchArray = {data:Array<DataFromStore>};    

const AddItem = ():JSX.Element => {
    const [item, setItem] = useState<ItemProps | null>(null);
    const handleItem = () => {
        setItem({item: 'Milky', price: 100});
    };
    useEffect(() => {
        if(item === null) return;
        fetch('http://localhost:3002/addItem', {
            method: 'POST',
            headers: {
